import React from "react";
import { useDispatch } from "react-redux";
import { setCategory } from "../features/category/categorySlice";

const categories = [
  {
    name: "Все",
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Искусство",
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Красота",
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Фитнес",
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Мода",
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Путешествия",
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Еда",
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Игры",
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Автомобили",
    image: "data:image/jpeg;base64,",
  },
];

export default function CategoryGrid() {
  const dispatch = useDispatch();

  const handleClick = (category) => {
    dispatch(setCategory(category));
  };

  return (
    <div className="px-4 py-6 grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
      {categories.map((cat) => (
        <div
          key={cat.name}
          onClick={() => handleClick(cat.name)}
          className="relative cursor-pointer rounded-2xl overflow-hidden shadow group"
        >
          <img
            src={cat.image}
            alt={cat.name}
            className="w-full h-36 object-cover transition-transform duration-300 group-hover:scale-110"
          />
          <div className="absolute inset-0 bg-black bg-opacity-30 flex items-center justify-center">
            <span className="text-white font-bold text-lg capitalize">
              {cat.name === "all" ? "All" : cat.name}
            </span>
          </div>
        </div>
      ))}
    </div>
  );
}
